import React, { useState, useEffect } from 'react'


const Typewriter = ({text, speed, delay}) => {
    const [displayText, setDisplayText] = useState('');
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const timeout = setTimeout(() => {
        const interval = setInterval(() => {
            if (currentIndex < text.length) {
                setDisplayText(prevText => prevText + text[currentIndex]);
                setCurrentIndex(prevIndex => prevIndex + 1);
            } else {
                clearInterval(interval);
            }
        }, speed);

        return () => clearInterval(interval);
    }, delay);
    return () => clearTimeout(timeout);
    }, [currentIndex, text, speed, delay]);
    return <>{displayText}</>
};

const Skills = () => {
    return (
        <div className="home-education">
            <ul className="home-education-skills">
                <li className="skills">
                <Typewriter text="Full Stack Web Developer" speed={100} delay={100}/>
                </li>
                <li className="skills">
                <Typewriter text="Technical Support" speed={100} delay={200}/>
                </li>
                <li className="skills">
                <Typewriter text="UI/UX Designer" speed={100} delay={300}/>
                </li>
                <li className="skills">
                <Typewriter text="Graphic Designer" speed={100} delay={400}/>
                </li>
                <li className="skills">
                <Typewriter text="..." speed={100} delay={500}/>
                </li>
            </ul>
        </div>
    )
}

export default Skills
