import React, { useState, useEffect } from 'react'


const Typewriter = ({ text, speed, delay }) => {
    const [displayText, setDisplayText] = useState('');
    const [currentIndex, setCurrentIndex] = useState(0);
  
    useEffect(() => {
      let timeout;
      let interval;
  
      const startTyping = () => {
        interval = setInterval(() => {
          if (currentIndex < text.length) {
            setDisplayText(prevText => prevText + text[currentIndex]);
            setCurrentIndex(prevIndex => prevIndex + 1);
          } else {
            clearInterval(interval);
            setCurrentIndex(0); // Reset currentIndex for the next line
            clearTimeout(timeout); // Clear the timeout to prevent multiple executions
            timeout = setTimeout(startTyping, delay); // Start typing the next line after delay
          }
        }, speed);
      };
  
      timeout = setTimeout(startTyping, delay);
  
      return () => {
        clearInterval(interval);
        clearTimeout(timeout);
      };
    }, [text, speed, delay, currentIndex]);
  
    return <>{displayText}</>;
  };

const Skills = () => {
    return (
        <div className="home-education">
            <ul className="home-education-skills">
                <li className="skills">
                <Typewriter text="sull Stack Web Developer" speed={100} delay={500}/>
                </li>
                <li className="skills">
                <Typewriter text="Technical Support" speed={100} delay={1000}/>
                </li>
                <li className="skills">
                <Typewriter text="UI/UX Designer" speed={100} delay={1500}/>
                </li>
                <li className="skills">
                <Typewriter text="Graphic Designer" speed={100} delay={2000}/>
                </li>
                <li className="skills">
                <Typewriter text="..." speed={100} delay={2500}/>
                </li>
            </ul>
        </div>
    )
}

export default Skills
