import React, { useState } from 'react';
import './projects.css';
import proj1 from '../../collateral/services/programming.jpg';
import proj2 from '../../collateral/services/programming.jpg';
import proj3 from '../../collateral/services/programming.jpg';
import proj4 from '../../collateral/services/programming.jpg';

const projectsdata = [
  {
    id: 1,
    image: proj1,
    title: "Programming",
    desc: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua."
  },
  {
    id: 2,
    image: proj2,
    title: "Technical Support",
    desc: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua."
  },
  {
    id: 3,
    image: proj3,
    title: "UI/UX Design",
    desc: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua."
  },
  {
    id: 4,
    image: proj4,
    title: "Graphic Design",
    desc: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua."
  }
]

const Projects = () => {
  const [selectedProject, setSelectedProject] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = (project) => {
    setSelectedProject(project);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <section className="services section">
      <h2 className="services-section-title">Projects <span className="services-section-title-line"></span></h2>
      <div className="services-container">
        {projectsdata.map((project) => (
          <div className="services-card-id" key={project.id} onClick={() => openModal(project)}>
            <img className="services-image" src={project.image} alt=''/>
            <h3 className="services-title">{project.title}</h3>
            <p className="services-description">{project.desc}</p>
          </div>
        ))}
      </div>
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>&times;</span>
            <img src={selectedProject.image} alt=''/>
            <h2>{selectedProject.title}</h2>
            <p>{selectedProject.desc}</p>
          </div>
        </div>
      )}
    </section>
  );
}

export default Projects;
